import React, { useState } from "react";
import { useSearchParams } from "react-router-dom";
import { commonTheme } from "../../utils/CommonTheme";
import Container from "../Container";
import CustomLink from "../CustomLink";
import FormContainer from "../form/FormContainer";
import FormInput from "../form/FormInput";
import Submit from "../form/Submit";
import Title from "../form/Title";
import {ImSpinner3} from "react-icons/im"

const ConfirmPassword = () => {
  const [isVerifying, setisVerified] = useState(true);
  const [searchParams] = useSearchParams();
  const token = searchParams.get("token");
  const id = searchParams.get("id");
  console.log(id);
  // http://localhost:3000/reset-password?token=596686eb2b7632b74f239f94eafafa1d71a67f4643b7a0e07d3ea06a6bd1&%20id=641e81a6d1bf4a0004099f77
  if (isVerifying) return (
    <FormContainer>
      <Container>
        <div className=" text-ce
        ">

        <h1 className="  text-4xl font-semibold dark:text-dark-subtle text-primary">
          Please wait we are verifying your token!
        </h1>
        <ImSpinner3 className="animate-spin text-4xl" />
        </div>
      </Container>
    </FormContainer>
  );
  return (
    <FormContainer>
      <Container>
        <form className={commonTheme + " w-96"}>
          <Title>Enter New Password</Title>
          <FormInput
            label="New Password"
            placeholder="********"
            name="password"
            type="password"
          />
          <FormInput
            label="Confirm Password"
            placeholder="********"
            name="confirmpassword"
            type="password"
          />
          <Submit value="Confirm Password" />
        </form>
      </Container>
    </FormContainer>
  );
};

export default ConfirmPassword;
